from typing import List
from collections import Counter, defaultdict


class Solution:
    def findSubstring(self, s: str, words: List[str]) -> List[int]:
        word_len = len(words[0])
        len_s = len(s)
        comb_indices = []
        words_count = Counter(words)
        for i in range(word_len):
            words_streak = 0
            window = defaultdict(int)
            current_indices = i

            for j in range(i, len_s - word_len + 1, word_len):
                current_word = s[j: j+word_len]

                if current_word not in words_count:
                    current_indices = j + word_len
                    window = defaultdict(int)
                    words_streak = 0
                    continue

                words_streak += 1
                window[current_word] += 1

                while window[current_word] > words_count[current_word]:
                    window[s[current_indices:current_indices + word_len]] -= 1
                    current_indices += word_len
                    words_streak -= 1

                if words_streak == len(words):
                    comb_indices.append(current_indices)

        return comb_indices


# test
slt = Solution()
print(slt.findSubstring("aaa", ["a","a"]))
print(slt.findSubstring("",
                        ["dhvf","sind","ffsl","yekr","zwzq","kpeo","cila","tfty","modg","ztjg","ybty","heqg","cpwo","gdcj","lnle","sefg","vimw","bxcb"]))
print(slt.findSubstring("barfoothefoobar", ["foo","bar"]))
print(slt.findSubstring("wordgoodgoodgoodbestword", ["word","good","best","word"]))
print(slt.findSubstring("barfoofoobarthefoobarman", ["bar","foo","the"]))
assert slt.findSubstring("aaa", ["a","a"]) == [0,1]
assert slt.findSubstring("",
                         ["dhvf","sind","ffsl","yekr","zwzq","kpeo","cila","tfty","modg","ztjg","ybty","heqg","cpwo","gdcj","lnle","sefg","vimw","bxcb"]) == [935]
assert slt.findSubstring("barfoothefoobar", ["foo","bar"]) == [0,9]
assert slt.findSubstring("wordgoodgoodgoodbestword", ["word","good","best","word"]) == []
assert slt.findSubstring("barfoofoobarthefoobarman", ["bar","foo","the"]) == [6,9,12]
